import React, { useState } from 'react';
import { Card } from 'react-bootstrap';
import { ChatFeed, Message } from 'react-chat-ui';

export default function ChatBox() {
  const [messages, setMessages] = useState([
    new Message({
      id: 1,
      message: "I'm the recipient! (The person you're talking to)"
    }), // Gray bubble
    new Message({ id: 0, message: "I'm you -- the blue bubble!" }), // Blue bubble
    new Message({
      id: 1,
      message: "I'm the recipient! (The person you're talking to)"
    }), // Gray bubble
    new Message({ id: 0, message: "I'm you -- the blue bubble!" }), // Blue bubble
    new Message({ id: 0, message: "I'm you -- the blue bubble!" }) // Blue bubble
  ]);

  return (
    <>
      <Card>
        <Card.Header>
          <div className='headDiv'>
            <p className='userName'>Name</p>
            <span class='logged-in'>‚óè</span>
          </div>
        </Card.Header>
        <Card.Body>
          <div className='messageDisplay'>
            <ChatFeed
              messages={messages} // Array: list of message objects
              // isTyping={true} // Boolean: is the recipient typing
              hasInputField={false} // Boolean: use our input, or use your own
              showSenderName // show the name of the user who sent the message
              bubblesCentered={false} //Boolean should the bubbles be centered in the feed?
            />
          </div>
          <div style={{ height: '5%', marginBottom: '30px' }}>
            <form
              // onSubmit={e => this.onMessageSubmit(e)}
              className='form'
            >
              <input className='text' placeholder='Type a message here...' />
              <input className='sendButton' type='submit' value='Send'></input>
            </form>
          </div>
        </Card.Body>
      </Card>
    </>
  );
}
