import React, { useContext, useEffect, useState } from 'react';
import { Badge, Card, ListGroup } from 'react-bootstrap';
import RoomModal from '../modals/roomModal';

export default function List(props) {
  const [users, setUsers] = useState([]);
  const [roomname, setroomname] = useState('');
  const [isRoomModalOpen, setRoomModalOpen] = useState(false);

  useEffect(() => {
    if (props.user && users.length) {
      let temp = users.filter(el => el.name !== props.user.name);
      setUsers(temp);
    }
  }, [props, users]);

  useEffect(() => {
    props.socket.on('usersList', users => {
      setUsers(users.users);
    });
  });
  const handleUserClick = (e, user) => {
    props.handleSelectedUser(e, user);
    setRoomModalOpen(true);
  };
  const handleModalClose = () => {
    setRoomModalOpen(false);
  };
  return (
    <>
      <RoomModal
        isRoomModalOpen={isRoomModalOpen}
        handleClose={handleModalClose}
        setroomname={setroomname}
        roomname={roomname}
        socket={props.socket}
        selectedUser={props.selectedUser}
      />
      <Card>
        <Card.Body>
          <ListGroup>
            {users.map((el, index) => (
              <ListGroup.Item
                key={index}
                as='li'
                className='d-flex justify-content-between align-items-start listItem'
                onClick={e => handleUserClick(e, el)}
              >
                <div className='ms-2 me-auto'>
                  <div className='bold'>{el.name}</div>
                  <p>{el.phone}</p>
                </div>
                <Badge className='avatar' variant='primary' pill>
                  <i className='fas fa-user-plus'></i>
                </Badge>
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Card.Body>
      </Card>
    </>
  );
}
