import React, { useContext, useEffect, useState } from 'react';
import { Col, Row } from 'react-bootstrap';
import { useHistory } from 'react-router-dom';
import UserContext from '../../userContext/UserContext';
import ChatBox from '../chat/chatBox';
import List from '../usersList/list';
import NavBar from './navbar';
import Api from '../../api/Api';
import { getAllUsersApi } from '../../constants';
export default function Welcome(props) {
  const [user, setUser] = useState({});
  const [selectedUser, setSelectedUser] = useState({});
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [state, userDispatch] = useContext(UserContext);
  let history = useHistory();

  useEffect(() => {
    let data = localStorage.getItem('userData');
    if (data.length) {
      Api.get(`${process.env.REACT_APP_BASE_URL}${getAllUsersApi}`)
        .then(res => {
          console.log(res);
        })
        .catch(err => console.log(err));
      setUser(JSON.parse(data));
      userDispatch({ type: 'SET_USER', payload: JSON.parse(data) });
      // props.socket.emit('userConnection', { name: JSON.parse(data).name });
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  useEffect(() => {
    if (user.userId) {
      props.socket.emit('addUser', user.userId);
      props.socket.on('getUsers', users => {
        console.log(users);
        // setOnlineUsers(
        //   user.followings.filter((f) => users.some((u) => u.userId === f))
        // );
      });
    }
  }, [user]);
  const handleLogOut = async () => {
    // props.socket.emit('userDisconection', { name: state.user.name });
    await userDispatch({ type: 'LOGOUT' });
    history.push('/login');
  };
  const handleSelectedUser = (e, user) => {
    setSelectedUser(user);
  };

  return (
    <>
      <NavBar user={user} handleLogOut={handleLogOut} />
      <div className='mainContent'>
        <Row className='mainContentRow'>
          <Col xs lg='4'>
            <List
              handleSelectedUser={handleSelectedUser}
              socket={props.socket}
              selectedUser={selectedUser}
              user={user}
            />
          </Col>
          <Col xs lg='8'>
            <ChatBox selectedUser={selectedUser} />
          </Col>
        </Row>
      </div>
    </>
  );
}
