export const initialState = {
  user: ''
};

export const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'SET_USER':
      localStorage.setItem('userData', JSON.stringify(action.payload));
      return {
        ...state,
        user: action.payload
      };
    case 'LOGOUT':
      localStorage.clear();
      return {
        ...state,
        state: initialState
      };
    default:
      return null;
  }
};
