import React, { useContext, useEffect, useState } from 'react';
import { Col, Row } from 'react-bootstrap';
import { useHistory } from 'react-router-dom';
import UserContext from '../../userContext/UserContext';
import ChatBox from '../chat/chatBox';
import List from '../usersList/list';
import NavBar from './navbar';
export default function Welcome(props) {
  const [selectedUser, setSelectedUser] = useState({});
  const [, userDispatch] = useContext(UserContext);
  let history = useHistory();
  const user = {};
  useEffect(() => {
    let data = localStorage.getItem('userData');
    if (JSON.parse(data)) {
      user = JSON.parse(data);
      userDispatch({ type: 'SET_USER', payload: JSON.parse(data) });
      props.socket.emit('userConnection', { name: JSON.parse(data).name });
    }
  }, []);

  const handleLogOut = async () => {
    await userDispatch({ type: 'LOGOUT' });
    history.push('/login');
  };
  const handleSelectedUser = (e, user) => {
    setSelectedUser(user);
  };

  return (
    <>
      <NavBar user={user} handleLogOut={handleLogOut} />
      <div className='mainContent'>
        <Row className='mainContentRow'>
          <Col xs lg='4'>
            <List
              handleSelectedUser={handleSelectedUser}
              socket={props.socket}
              selectedUser={selectedUser}
              user={user}
            />
          </Col>
          <Col xs lg='8'>
            <ChatBox selectedUser={selectedUser} />
          </Col>
        </Row>
      </div>
    </>
  );
}
