import React, { useContext, useEffect, useState } from 'react';
import { Badge, Card, Col, Container, ListGroup, Row } from 'react-bootstrap';
import { ChatFeed, Message } from 'react-chat-ui';
import { useHistory } from 'react-router-dom';
import UserContext from '../../userContext/UserContext';
import List from '../usersList/list';
import NavBar from './navbar';

export default function Welcome() {
  const [user, setUser] = useState({});
  const [, userDispatch] = useContext(UserContext);
  let history = useHistory();

  useEffect(() => {
    let data = localStorage.getItem('userData');
    if (data) {
      setUser(JSON.parse(data));
      userDispatch({ type: 'SET_USER', payload: JSON.parse(data) });
    }
  }, []);

  const handleLogOut = async () => {
    await userDispatch({ type: 'LOGOUT' });
    history.push('/login');
  };
  const messages = [
    new Message({
      id: 1,
      message: "I'm the recipient! (The person you're talking to)"
    }), // Gray bubble
    new Message({ id: 0, message: "I'm you -- the blue bubble!" }) // Blue bubble
  ];
  return (
    <>
      <NavBar user={user} handleLogOut={handleLogOut} />
      <div className='mainContent'>
        <Row className='mainContentRow'>
          <Col xs lg='4'>
            <List />
          </Col>
          <Col xs lg='8'>
            <Card>
              <Card.Header>
                <div className='headDiv'>
                  <p className='userName'>Name</p>
                  <span class='logged-in'>‚óè</span>
                </div>
              </Card.Header>
              <Card.Body>
                <div className='messageDisplay'>
                  <ChatFeed
                    messages={messages} // Array: list of message objects
                    isTyping={true} // Boolean: is the recipient typing
                    hasInputField={false} // Boolean: use our input, or use your own
                    showSenderName // show the name of the user who sent the message
                    bubblesCentered={false} //Boolean should the bubbles be centered in the feed?
                    // JSON: Custom bubble styles
                    bubbleStyles={{
                      text: {
                        fontSize: 30
                      },
                      chatbubble: {
                        borderRadius: 70,
                        padding: 40
                      }
                    }}
                  />
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}
