import React, { useContext, useEffect, useState } from 'react';
import { Col, Row } from 'react-bootstrap';
// import socketIOClient from 'socket.io-client';
import openSocket from 'socket.io-client';
import { useHistory } from 'react-router-dom';
import UserContext from '../../userContext/UserContext';
import ChatBox from '../chat/chatBox';
import List from '../usersList/list';
import NavBar from './navbar';
const ENDPOINT = process.env.REACT_APP_BASE_URL;
export default function Welcome() {
  const [user, setUser] = useState({});
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState({});
  const [, userDispatch] = useContext(UserContext);
  const socket = openSocket(ENDPOINT, { transports: ['websocket'] });
  let history = useHistory();

  useEffect(() => {
    let data = localStorage.getItem('userData');
    if (data) {
      socket.emit('user connection', data);
      setUser(JSON.parse(data));
      userDispatch({ type: 'SET_USER', payload: JSON.parse(data) });
    }
  }, []);
  useEffect(() => {
    let data = localStorage.getItem('userData');
    if (data) {
      socket.emit('user connection', data);
    }
  });
  useEffect(() => {
    socket.on('connected users list', users => {
      console.log(JSON.parse(users));
      let onlineUser = JSON.parse(users);
      let temp = [];
      if (onlineUser.email !== user.email) {
        temp.push(onlineUser);
        setOnlineUsers(temp);
      }
    });
  });
  useEffect(() => {
    return () => {
      socket.emit('disconnect user', () => {});
    };
  }, []);
  const handleLogOut = async () => {
    await userDispatch({ type: 'LOGOUT' });
    history.push('/login');
  };
  const handleSelectedUser = (e, user) => {
    setSelectedUser(user);
  };

  return (
    <>
      <NavBar user={user} handleLogOut={handleLogOut} />
      <div className='mainContent'>
        <Row className='mainContentRow'>
          <Col xs lg='4'>
            <List handleSelectedUser={handleSelectedUser} />
          </Col>
          <Col xs lg='8'>
            <ChatBox selectedUser={selectedUser} />
          </Col>
        </Row>
      </div>
    </>
  );
}
